meta._wpb_post_custom_css:
.wh-column-offset {
    margin-left: 13%;
}
.wh-box-big {
    padding-left: 15px;
    padding-right: 23px;
}

.wh-box-big-left {
    padding-left: 10px;
}
.wh-icon-box .vc_column-inner:hover {
    border-color: #d2d2d2;
    box-shadow: 0px 1px 1px #DCDFE8
}
.wh-button-float-right {
    float: right;
}
.wh-post-list h3 a {
    font-weight: normal;
}

.wh-box-shadow .vc_column-inner {
    box-shadow: 0px 1px 1px #DCDFE8;
}
#contact .wpcf7-textarea {
    height: 206px;
}
.wh-home-gallery {
    width: 120%;
    margin-left: -10% !important;
}
.wh-testimonial-stars i {
    color: gold;
    margin-right: 10px;
    font-size: 13px;
}
.wh-map-embellishment {
    position: absolute;
    left: 50%;
    margin-left: -48px !important;
    z-index: 999;
}
.wh-home-quote {
    max-width: 300px;
}
.wh-footer-icons {
    width: 310px;
    margin: 0 auto;
}
@media (max-width: 1400px)  { 
.wh-home-gallery {
    width: auto;
    margin-left: -15px !important;
}
}
@media (max-width: 1200px)  { 
.wh-box-big {
    padding-right: 15px;
    margin-bottom: 35px;
}
.wh-box-big-left {
    padding-left: 0;
}
}
@media (max-width: 767px)  { 
.wh-column-offset {
    margin-left: 0;
}
.wh-reset-margin-left .vc_column-inner {
    margin-left: 0 !important;
}
}

.wh-header-button {
    text-align: left;
    padding-left: 67px;
    padding-top: 27px;
    padding-bottom: 25px;
    border-radius: 0;
    padding-right: 25px;
    position: relative;
    float: right;
}
.wh-header-button a {
    color: #fff;
}
.wh-header-button i {
    position: absolute;
    left: 28px;
    font-size: 27px;
}
.wh-header-button span {
    font-size: 12px;
    display: block;
    text-transform: none;
    margin-top: 4px;
}
.wh-header-link {
    float: right;
}
